import React from 'react';
import './App.css';
import logo from './Images/logo.png';
import woofer from './Images/woofer.png';

export default class Header extends React.Component {
  state = {
    addClass: false,
    addClass2: false,
  };
  addMenu = () => {
    this.setState(({ addClass }) => {
      return {
        addClass: !addClass,
      };
    });
  };

  register = () => {
    this.setState(({ addClass2 }) => {
      return {
        addClass2: !addClass2,
      };
    });
  };

  render() {
    return (
      <div className='header'>
        <div className='hed'>
          <img src={logo} />
          <ul>
            <li>HOME</li>
            <li>ABOUT</li>
            <li>SERVICE</li>
            <li>TESTIMONIAL</li>
            <li>CONTACT US</li>
          </ul>
          <div className={`hamburger ${this.state.addClass ? 'burg' : ''}`}>
            <p onClick={this.addMenu}>
              <i class='fas fa-bars'></i>
            </p>
            <span onClick={this.addMenu}>
              <i class='far fa-arrow-alt-circle-left'></i>
            </span>
          </div>
          <div
            className={`hamburgerMenu ${
              this.state.addClass ? 'menuBurger' : ''
            }`}
          >
            <p>HOME</p>
            <p>ABOUT</p>
            <p>SERVICE</p>
            <p>TESTIMONIAL</p>
            <p>CONTACT US</p>
          </div>
        </div>
        <div className='hed2'>
          <div className='hed22'>
            <h1>Unlimited Web Hosting</h1>
            <p>
              <b>
                Donec nec justo eget felis facilisis fermentum. Aliquam
                porttitor
                <br />
                mauris sit amet orci. Aenean dignissim pellentesque felis.Donec
                <br />
                nec justo eget felis facilisis fermentum. Aliquam porttitor
                mauris
                <br />
                sit amet orci. Aenean dignissim pellentesque felis.
                <br />
              </b>
            </p>
            <div className='hed2span'>
              <span>Get Started</span>
              <span className='span' onClick={this.register}>
                Contact Us
              </span>
            </div>
          </div>
          <div className={`${this.state.addClass2 ? 'register' : ''}`}>
            <span>
              <i class='far fa-arrow-alt-circle-left'></i>
            </span>
          </div>
          <img src={woofer} />
        </div>
        <div className='hed2Input'>
          <input type='text' placeholder='Enter domain name here' />
          <button class='search_bt' type='Subscribe'>
            Search
          </button>
        </div>
      </div>
    );
  }
}
