import React from "react";
import { Field, reduxForm } from "redux-form";
import { recuired } from "../conponents/common/validators/validator";
import { Input } from "../conponents/common/FormsControls/formscontrols";
import { authLoginPAge } from "../redux/authPage";

import { connect } from "react-redux";
import { Redirect } from "react-router-dom";

const LoginForm = (props) => {
  return (
    <form onSubmit={props.handleSubmit}>
      <div>
        <Field
          placeholder={"Email"}
          name={"email"}
          component={Input}
          validate={[recuired]}
        />
      </div>
      <div>
        {" "}
        <Field
          placeholder={"password"}
          name={"password"}
          component={Input}
          validate={[recuired]}
          type={"password"}
        />
      </div>
      <div>
        {" "}
        <Field
          type={"checkbox"}
          component={Input}
          validate={[recuired]}
          name={"rememberMe"}
        />
        remember me
      </div>
      <div>
        {" "}
        <button> login </button>
      </div>
    </form>
  );
};

const LoginReduxForm = reduxForm({
  form: "login",
})(LoginForm);

const Login = (props) => {
  const onSubmit = (formData) => {
    props.authLoginPAge(formData.email, formData.password, formData.rememberMe);
  };

  if (props.isAuth) {
    return <Redirect to={"/profile"} />;
  }

  return (
    <div>
      <h1>LOGIN</h1>
      <LoginReduxForm onSubmit={onSubmit} />
    </div>
  );
};

export default connect(null, { authLoginPAge })(Login);
