import React from 'react';
import './SectionOne.css';

export default class SectionOne extends React.Component {
  state = {
    bar: false,
  };

  Ber = () => {
    this.setState(({ bar }) => {
      return {
        bar: !bar,
      };
    });
  };

  render() {
    return (
      <div className='sectionone'>
        <i class='fas fa-heartbeat' onClick={this.Ber}></i>

        <h2 className={`ba ${this.state.bar ? 'ba2' : ''}`}> </h2>
      </div>
    );
  }
}
