import { connect } from "react-redux";
import { ch, fl, setTotalUserCountAC, su, uw } from "../../redux/users";
import New from "./New";
import React from "react";
import n from "./New.module.css";
import * as axios from "axios";
import userPhoto from "../../image/down2.jpg";
import Users from "./New";

class UsersCn extends React.Component {
  componentDidMount() {
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${this.props.mpageq}&count=${this.props.mklient}`
      )
      .then((response) => {
        this.props.setusers(response.data.items);
        this.props.setTotalUserCount(response.data.totalCount);
      });
  }
  onPageChanged = (usermk) => {
    this.props.setch(usermk);
    axios
      .get(
        `https://social-network.samuraijs.com/api/1.0/users?page=${usermk}&count=${this.props.mklient}`
      )
      .then((response) => {
        this.props.setusers(response.data.items);
      });
  };

  render() {
    return <Users />;
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.usersReducer.users,
    mpage: state.usersReducer.mpage,
    totalUsersCount: state.usersReducer.totalUsersCount,
    mpageq: state.usersReducer.mpageq,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    folow: (userID) => {
      dispatch(fl(userID));
    },
    unfolow: (userID) => {
      dispatch(uw(userID));
    },
    setusers: (user) => {
      dispatch(su(user));
    },
    setch: (userch) => {
      dispatch(ch(userch));
    },
    setTotalUserCount: (totalCount) => {
      dispatch(setTotalUserCountAC(totalCount));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(UsersCn);
