import React from "react";

export class Status extends React.Component {
  state = {
    editMode: false,
  };

  activateEditMode() {
    this.setState({
      editMode: true,
    });
  }

  deactivateEditMode() {
    this.setState({
      editMode: false,
    });
  }

  render() {
    return (
      <>
        <div>
          <p onDoubleClick={this.activateEditMode.bind(this)}>
            {this.props.status}
          </p>
        </div>
        <div>
          <input
            autoFocus={true}
            onBlur={this.deactivateEditMode.bind(this)}
            value={this.props.status}
          />
        </div>
      </>
    );
  }
}
