import React from 'react';

export default class HedM extends React.Component {
  state = {
    addClass: false,
  };
  addU = () => {
    this.setState(({ addClass }) => {
      return {
        addClass: !addClass,
      };
    });
  };

  render() {
    return (
      <div className='hedm'>
        <div className='hedm2'>
          <div className='hedm-h1'>
            <h1>TREALOP</h1>
          </div>
          <ul className='ul'>
            <li className='li2'>HOME</li>
            <li>PAGE</li>
            <li>DROPDOWN</li>
            <li>LINK TEXT</li>
            <li>LINK TEXT</li>
            <li>LINK TEXT</li>
            <li>LINK TEXT</li>
          </ul>
          <div
            className={`select ${addClass ? 'select2' : ''}`}
            onClick={this.addU}
          >
            MENU
            <ul>
              <li>MENU</li>
              <li>HOME</li>
              <li>PAGES</li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
