import React from "react";
// import People from './People';


export default class Person extends React.Component{

 state = {
    style2: {
        backgroundColor: "red"
      },

      done: false
}


    

    enterPerson = () => {

        console.log("enter")
        this.setState(({done}) => {
            return {
        done: !done
        }})
      }
    render() {

       const style = {
           border: "2px solid",
           width: "250px",
           margin: "0 auto",
        
       } 
       const {done} = this.state;
let newClass = "list";
       if(done) {
           newClass += " new"
       }
   
    // console.log(item)
    return (
        <div style={style}  className={newClass}>
            <h2>{this.props.name}</h2>
           <p>
      {this.props.proff}
        </p>
        <strong>{this.props.adress}</strong>
        <p>{this.props.description}</p>
        {/* <People /> */}
        <button onClick={this.enterPerson}>click</button>
        <button >deleted</button>
        </div>
    )
    }
}
