import avatarka from '../image/down2.jpg';

let store = {
  _rrerenderInfo() {},

  _state: {
    myPage: {
      person: [
        { name: 'Sasha', id: '1' },
        { name: 'Tyom', id: '2' },
        { name: 'Viktoria', id: '3' },
        { name: 'Ann', id: '4' },
        { name: 'Ani', id: '5' },
      ],
      messages: [
        { msg: 'Hi' },
        { msg: 'How is your komasutra?' },
        { msg: 'Yo' },
      ],
    },
    messagePage: {
      mymsg: [
        { img: avatarka, ike: 'like', msg: 'barior axperner', id: 1 },
        { img: avatarka, ike: 'like', msg: 'inchka chka', id: 2 },
      ],
      adText: 'yo yo',
    },
    friendsPage: {
      friend: [{ title: 'FRIENDS' }],
    },
  },
  add() {
    let addPost = {
      msg: _state.messagePage.adText,
      img: avatarka,
      ike: 'like',
    };
    _state.messagePage.mymsg.push(addPost);
    _state.messagePage.adText = '';
    _rrerenderInfo(state);
  },
};

export let addPost = (text) => {
  state.messagePage.adText = text;
  rrerenderInfo(state);
};

export let subscribe = (observer) => {
  rrerenderInfo = observer;
};

export default state;
