import React from 'react';
import Hed from './Hed';
import HedM from './HedM';
import HedH from './HedH';
import HedP from './HedP';
import ToDo from './Todo';
import './App.css';
import { Route } from 'react-router';

export default class Header extends React.Component {
  state = {
    person: [{ name: 'react', id: 1 }],
  };

  addItem = (text) => {
    const newList = {
      name: text,
      id: this.newId++,
    };
    this.setState(({ person }) => {
      const myLastTodo = [...person, newList];
      return { person: myLastTodo };
    });
  };
  render() {
    return (
      <header>
        <Hed />
        <HedM />
        <Route path={'/'} exact component={HedH} />
        <Route path={'/page'} exact component={HedP} />
        <ToDo />
      </header>
    );
  }
}
