import { connect } from "react-redux";
import {
  folow,
  getVUsers,
  isFLUN,
  setch,
  unfolow,
  VUfolow,
  VUunfolow,
} from "../../redux/users";
import React from "react";
import Users from "./New";
import Preloader from "../../conponents/common/Prealoader/Preloader";
import { WithAuthRedirect } from "../hoc/AuthRedirect";
import { compose } from "redux";
import {
  getCurrentPage,
  getFollowingProgress,
  getIsFetching,
  getPageSize,
  getUsers,
  getUsersTotalCount,
} from "../../redux/users-selector";

class UsersCn extends React.Component {
  componentDidMount() {
    this.props.getVUsers(this.props.mpageq, this.props.mklient);
  }
  onPageChanged = (usermk) => {
    this.props.getVUsers(usermk, this.props.mklient);
  };

  render() {
    return (
      <>
        {this.props.isFetching ? <Preloader /> : null}
        <Users {...this.props} onPageChanged={this.onPageChanged} />
      </>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: getUsers(state),
    mpage: getPageSize(state),
    totalUsersCount: getUsersTotalCount(state),
    mpageq: getCurrentPage(state),
    isFetching: getIsFetching(state),
    nkisFLUN: getFollowingProgress(state),
  };
};

export default compose(
  connect(mapStateToProps, {
    folow,
    unfolow,
    setch,
    isFLUN,
    getVUsers,
    VUunfolow,
    VUfolow,
  })
  // WithAuthRedirect
)(UsersCn);
