import React from 'react';
import './App.css';

export default class App extends React.Component {
  state = {
    pageTitle: 'privet',
    person: 'click me',
    newPage: 'smem privet',
  };

  changeTitle = () => {
    this.setState({
      pageTitle: this.state.newPage,
    });
  };

  changeInput = (event) => {
    this.setState({
      pageTitle: event.target.value,
    });
  };

  handleClick = () => {
    alert('inchka chka');
  };

  render() {
    const styles = {
      border: '3px solid black',
      width: '100px',
      height: '50px',
      cursor: 'pointer',
      background: 'black',
      color: 'white',
      fontSize: '20px',
    };
    return (
      <div className='App'>
        <h1>{this.state.pageTitle}</h1>
        <button style={styles} onClick={this.changeTitle}>
          {this.state.person}
        </button>
        <input type='text' onChange={this.changeInput}></input>
      </div>
    );
  }
}
