import React from 'react';
import './SectionOne.css';

export default class SectionOne extends React.Component {
  state = {
    bar: false,
    gar: true,
  };

  Ber = () => {
    this.setState(({ bar }) => {
      return {
        bar: !bar,
      };
    });
  };
  Ger = () => {
    this.setState(({ gar }) => {
      return {
        gar: !gar,
      };
    });
  };

  render() {
    return (
      <div className='sectionone'>
        <i class='fas fa-heartbeat' onClick={this.Ber}></i>
        <i class='fas fa-campground' onClick={this.Ger}></i>
        <h2 className={`ba ${this.state.bar ? 'ba2' : ''}`}>
          <i class='fas fa-heartbeat' />
        </h2>
        <h2 className={`bs ${this.state.gar ? 'bs2' : ''}`}>
          <i class='fas fa-campground' />
        </h2>
      </div>
    );
  }
}
