import React from 'react';
import Log from './componentsel/Log';
import Logcr from './componentsel/Logcr';
import './Login.css';

export default class Login extends React.Component {
  state = {
    login: [{ name: 'chaxalyan.v@mail.ru', id: '1' }],
  };

  // Ren = (text) => {
  //   const newlogin = {
  //     name: text,
  //     id: this.newId++,
  //   };
  //   this.setState = ({ login }) => {
  //     const Mylogin = [...login, newlogin];
  //     return { login: Mylogin };
  //   };
  // };
  Ren = (text) => {
    const newList = {
      name: text,
      id: this.newId++,
    };
    this.setState(({ person }) => {
      const myLastTodo = [...person, newList];
      return { person: myLastTodo };
    });
  };

  render() {
    const { login } = this.state;
    return (
      <div className='login'>
        <Log Ren={this.Ren} />
        {login.map((item) => {
          return <Logcr {...item} />;
        })}
      </div>
    );
  }
}
