import React from "react";
import n from "./New.module.css";
import userPhoto from "../../image/down2.jpg";
import { NavLink } from "react-router-dom";
import { UsersApi } from "../../API/api";

let Users = (props) => {
  let tiv = Math.ceil(props.totalUsersCount / props.mpage);

  let usert = [];
  for (let i = 1; i <= tiv; i++) {
    usert.push(i);
  }

  return (
    <div className={n.item}>
      {usert.map((p) => {
        return (
          <span
            className={props.mpageq === p && n.ba}
            onClick={(e) => {
              props.onPageChanged(p);
            }}
          >
            {p}
          </span>
        );
      })}
      {props.users.map((b) => {
        return (
          <div key={b.id}>
            <NavLink to={/profile/ + b.id}>
              <img src={b.photos.small != null ? b.photos.small : userPhoto} />
            </NavLink>
            <p>{b.name}</p>
            <p>{b.status}</p>
            <p>{"b.location.city"}</p>
            <p>{"b.location.country"}</p>
            {b.followed ? (
              <button
                disabled={props.nkisFLUN.some((id) => id === b.id)}
                onClick={() => {
                  props.isFLUN(true, b.id);
                  this.props.VUfolow(b.id);
                }}
              >
                unfolow
              </button>
            ) : (
              <button
                disabled={props.nkisFLUN.some((id) => id === b.id)}
                onClick={() => {
                  props.isFLUN(true, b.id);
                  UsersApi.getUsers3(b.id).then((data) => {
                    if (data.resultCode === 0) {
                      props.folow(b.id);
                    }
                    props.isFLUN(false, b.id);
                  });
                }}
              >
                follow
              </button>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default Users;
