import React from 'react';
import { NavLink } from 'react-router-dom';

export default class HedM extends React.Component {
  state = {
    addClass: false,
  };
  addU = (e) => {
    e.stopPropagation();
    this.setState(({ addClass }) => {
      return {
        addClass: !addClass,
      };
    });
  };

  render() {
    return (
      <div className='hedm'>
        <div className='hedm2'>
          <div className='hedm-h1'>
            <h1>TREALOP</h1>
          </div>
          <ul className='ul'>
            <li className='li2'>
              <NavLink to='/'>HOME</NavLink>
            </li>
            <li>
              <NavLink to='/page'>PAGE</NavLink>
            </li>
            <li>DROPDOWN</li>
            <li>LINK TEXT</li>
            <li>LINK TEXT</li>
            <li>LINK TEXT</li>
            <li>LINK TEXT</li>
          </ul>
          <div
            className={`select ${this.state.addClass ? 'select2' : ''}`}
            onClick={this.addU}
          >
            MENU
            <ul>
              <li>MENU</li>
              <li>HOME</li>
              <li>PAGES</li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
