const DR = 'BRAT_JAN';
const DRR = 'AB_JAN';

const DDR = (state, action) => {
  switch (action.type) {
    case DR:
      let addMsg = {
        msg: state.newmsg,
      };
      state.messages.push(addMsg);
      state.newmsg = '';

    case DRR:
      state.newmsg = action.newMsg;
  }
};

export const ar = () => ({
  type: DR,
});

export const er = (text) => ({
  type: DDR,
  newMsg: text,
});

export default DDR;
