import React from 'react';
import './SectionOne.css';

export default class SectionOne extends React.Component {
  state = {
    icons: [
      {
        icon: <i class='fas fa-heartbeat'></i>,
        title: 'Heart Rate Monitor',
        text:
          'Lorem Ipsum is simply dummy text of the printing and typesetting industry.',
      },
      {
        icon: <i class='fas fa-briefcase-medical'></i>,
        title: 'Blood Pressure Manager',
        text:
          'Lorem Ipsum is simply dummy text of the printing and typesetting industry.',
      },
      {
        icon: <i class='fas fa-bell'></i>,
        title: 'Alarm Alert',
        text:
          'Lorem Ipsum is simply dummy text of the printing and typesetting industry.',
      },
    ],
    iconss: [
      {
        icon: <i class='fas fa-map-marker-alt'></i>,
        title: 'Location Finder',
        text:
          'Lorem Ipsum is simply dummy text of the printing and typesetting industry.',
      },
      {
        icon: <i class='fas fa-fingerprint'></i>,
        title: 'Fingerprint Lock',
        text:
          'Lorem Ipsum is simply dummy text of the printing and typesetting industry.',
      },
      {
        icon: <i class='fas fa-mobile'></i>,
        title: 'Camera and Bluetooth',
        text:
          'Lorem Ipsum is simply dummy text of the printing and typesetting industry.',
      },
    ],
  };

  render() {
    const { icons, iconss } = this.state;
    return (
      <div className='section1'>
        <div className='section2'>
          <h3>WHY CHOOSING US</h3>
          <h2>
            Easy Process With<span> Best Features</span>
          </h2>
          <p className='secp'>
            Lorem Ipsum is simply dummy text of the printing and typesetting
            <br />
            industry. Lorem Ipsum has been the industry’s standard
          </p>
          <div className='section3'>
            {icons.map((ic) => {
              return (
                <div className='section33'>
                  <div className='section333'>
                    <p className='iconp'>{ic.icon}</p>
                    <h3>{ic.title}</h3>
                    <span>{ic.text}</span>
                  </div>
                  <div className='section33'>
                    <div className='section333 section4'>
                      <p className='iconp'>{ic.icon}</p>
                      <h3>{ic.title}</h3>
                      <span>{ic.text}</span>
                    </div>
                  </div>
                </div>
              );
            })}

            <div className=''></div>
          </div>
        </div>
      </div>
    );
  }
}
