const FOLOW = "FOLOW";
const UNFOLOW = "UNFOLOW";
const SET_USERS = "SET_USERS";

let initialState = {
  users: [
    {
      id: 1,
      followed: false,
      fullName: "Exo",
      status: "varavskoy",
      location: { city: "Erevan", country: "goshi bert" },
    },
    {
      id: 2,
      followed: true,
      fullName: "Gonch",
      status: "varavskoy",
      location: { city: "Erevan", country: "goshi bert" },
    },
    {
      id: 3,
      followed: false,
      fullName: "Ponch",
      status: "varavskoy",
      location: { city: "Erevan", country: "goshi bert" },
    },
  ],
};

const usersReducer = (state = initialState, action) => {
  switch (action.type) {
    case FOLOW:
      return {
        ...state,
        users: state.users.map((dd) => {
          if (dd.id === action.usersID) {
            return { ...dd, followed: true };
          }
          return dd;
        }),
      };
    case UNFOLOW:
      return {
        ...state,
        users: state.users.map((ddd) => {
          if (ddd.id === action.usersID) {
            return { ...ddd, followed: false };
          }
          return ddd;
        }),
      };
    case SET_USERS:
      return { ...state, users: [...state.users, ...action.state] };

    default:
      return state;
  }
};

export const fl = (usersID) => ({ type: FOLOW, usersID });
export const uw = (usersID) => ({ type: UNFOLOW, usersID });
export const su = (users) => ({ type: SET_USERS, users });

export default usersReducer;
