import MyPosts from './MyPosts';
import { arajin, erkrord } from '../../../redux/mr.js';
import { connect } from 'react-redux';

let mapStateToProps = (state) => {
  return {
    mymsg: state.messagePage.mymsg,
    adText: state.messagePage.adText,
  };
};

let mapDispatchToProps = (dispatch) => {
  return {
    add: () => {
      dispatch(arajin());
    },
    change: (text) => {
      let ac = erkrord(text);
      dispatch(ac);
    },
  };
};

const PostsMy = connect(mapStateToProps, mapDispatchToProps)(MyPosts);

export default PostsMy;
